// src/redux/reducers.js
const initialState = {
    selectedDate: null,
    selectedTime: null,
    name: '',
    client: '',
    bookedSlots: [],
  };
  
  const rootReducer = (state = initialState, action) => {
    console.log("Thiru Action 2222");
    switch (action.type) {
      case 'SET_DATE':
        return { ...state, selectedDate: action.payload };
      case 'SET_TIME':
        return { ...state, selectedTime: action.payload };
      case 'SET_NAME':
        return { ...state, name: action.payload };
      case 'SET_CLIENT':
        return { ...state, client: action.payload };
      case 'BOOK_TIME_SLOT':
        return {
          ...state,
          bookedSlots: [
            ...state.bookedSlots,
            {
              date: state.selectedDate.toISOString(),
              time: state.selectedTime.value,
              name: state.name,
              client: state.client,
            },
          ],
        };
      case 'LOAD_BOOKED_SLOTS':
        // Load booked slots from localStorage
        const storedBookedSlots = JSON.parse(localStorage.getItem('bookedSlots')) || [];
        return { ...state, bookedSlots: storedBookedSlots };
      case 'ADD_BOOKED_SLOT':
        console.log("Thiru Action");
        const storeTime = JSON.parse(localStorage.getItem('newBooking')) || [];
        return { ...state, bookedSlotss: storeTime };
      default:
        return state;
    }
  };
  
  export default rootReducer;
