// utils.js
export const generateTimeSlots = (duration, bookedSlots) => {
    const startTime = 6;
    const endTime = 21;
    const timeSlots = [];
  
    for (let hour = startTime; hour < endTime; hour++) {
      for (let minute = 0; minute < 60; minute += duration) {
        const time = `${String(hour).padStart(2, '0')}:${String(minute).padStart(2, '0')}`;
        const endTime = addDurationToTime(hour, minute, duration);
        const label = `${time} - ${endTime}`;
        const isSlotBooked = isSlotAlreadyBooked(time, endTime, bookedSlots);
  
        if (!isSlotBooked) {
          timeSlots.push({ label, value: { startTime: time, endTime } });
        }
      }
    }
  
    return timeSlots;
  };
  
  const addDurationToTime = (hour, minute, duration) => {
    let nextHour = hour;
    let nextMinute = minute + duration;
  
    if (nextMinute >= 60) {
      nextHour += Math.floor(nextMinute / 60);
      nextMinute %= 60;
    }
  
    return `${String(nextHour).padStart(2, '0')}:${String(nextMinute).padStart(2, '0')}`;
  };
  
  const isSlotAlreadyBooked = (startTime, endTime, bookedSlots) => {
    console.log(startTime)
    console.log(endTime)
