// store.js
import { createStore } from 'redux';

const initialState = {
  bookedSlots: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_BOOKED_SLOT':
      return {
        ...state,
        bookedSlots: [...state.bookedSlots, action.payload],
      };
    default:
      return state;
  }
};

const store = createStore(reducer);

export default store;
