export const generateTimeSlots = (duration, bookedSlots = []) => {
    const startTime = 6;
    const endTime = 21;
    const timeSlots = [];
  
    let currentMinute = 0;
  
    for (let hour = startTime; hour < endTime; hour++) {
      for (let minute = currentMinute; minute < 60; minute += duration) {
        const currentTime = `${String(hour).padStart(2, '0')}:${String(minute).padStart(2, '0')}`;
  
        // Check if the current time slot is already booked
        if (bookedSlots.includes(currentTime)) {
          currentMinute = minute + duration; // Skip to the next available time slot
          continue;
        }
  
        const nextHour = hour + Math.floor((minute + duration) / 60);
        const nextMinute = (minute + duration) % 60;
        const nextTime = `${String(nextHour).padStart(2, '0')}:${String(nextMinute).padStart(2, '0')}`;
  
        const label = `${currentTime} - ${nextTime}`;
        timeSlots.push({ label, value: currentTime });
  
        currentMinute = nextMinute;
      }
  
      currentMinute = 0;
    }
  
    return timeSlots;
  };
