import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addBookedSlot } from './redux/actions';
import { loadBookedSlots } from './redux/actions';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import Select from 'react-select';
import './App.css';

const generateTimeSlots = (duration) => {
  const startTime = 6;
  const endTime = 21;
  const timeSlots = [];

  for (let hour = startTime; hour < endTime; hour++) {
    for (let minute = 0; minute < 60; minute += duration) {
      const time = `${String(hour).padStart(2, '0')}:${String(minute).padStart(2, '0')}`;
      const nextHour = hour + Math.floor((minute + duration) / 60);
      const nextMinute = (minute + duration) % 60;
      const label = `${time} - ${String(nextHour).padStart(2, '0')}:${String(nextMinute).padStart(2, '0')}`;
      timeSlots.push({ label, value: time });
    }
  }

  return timeSlots;
};

const App = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);
  const [name, setName] = useState('');
  const [client, setClient] = useState('');
  const [selectedDuration, setSelectedDuration] = useState({ label: '30 minutes', value: 30 });

  const dispatch = useDispatch();
  const bookedSlots = useSelector((state) => state.bookedSlots);

  const durationOptions = [
    { label: '30 minutes', value: 30 },
    { label: '60 minutes', value: 60 },
    { label: '90 minutes', value: 90 },
    { label: '120 minutes', value: 120 },
    { label: '150 minutes', value: 150 }
  ];

  const handleDurationChange = (selectedOption) => {
    setSelectedDuration(selectedOption);
    setSelectedTime(null);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
    setSelectedTime(null);
  };

  const handleTimeChange = (time) => {
    setSelectedTime(time);
  };

  const bookTimeSlot = () => {
    const isSlotBooked = bookedSlots.some(
      (slot) => slot.date === selectedDate.toISOString() && slot.time === selectedTime.value
    );

    if (isSlotBooked) {
      alert('Time slot already booked with a name. Please choose another slot.');
    } else {
      const newBooking = { date: selectedDate.toISOString(), time: selectedTime.value, name, client };
      dispatch(addBookedSlot(newBooking));
      alert(`Booking successful for ${selectedTime.label} on ${selectedDate.toDateString()} for ${name} with client ${client}`);
    }
  };

  useEffect(() => {
    dispatch(loadBookedSlots());
  }, [dispatch]);

  useEffect(() => {
    localStorage.setItem('bookedSlots', JSON.stringify(bookedSlots));
  }, [bookedSlots]);

  return (
    <div className="App">
      <div className="booking-container">
        <h2>Select Date</h2>
        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          minDate={new Date()}
          dateFormat="MMMM, yyyy"
        />

        {selectedDate && (
          <>
            <h2>Select Time</h2>
            <Select
              options={generateTimeSlots(selectedDuration.value).filter(
                (option) =>
                  !bookedSlots.some(
                    (slot) => slot.date === selectedDate.toISOString() && slot.time === option.value
                  )
              )}
              value={selectedTime}
              onChange={handleDurationChange}
            />

            <h2>Enter Details</h2>
            <label>Name:</label>
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} />

            <label>Client:</label>
            <input type="text" value={client} onChange={(e) => setClient(e.target.value)} />

            <button onClick={bookTimeSlot}>Book Slot</button>
          </>
        )}
      </div>

      <div className="booked-slots-container">
        <div className="booked-slots-left">
          <h2>Booked Slots</h2>
          {bookedSlots.map((bookedSlot, index) => (
            <div key={index} className="booked-slot">
              <p>{new Date(bookedSlot.date).toLocaleDateString()} - {bookedSlot.time}</p>
            </div>
          ))}
        </div>

        <div className="booked-slots-right">
          <h2>Details</h2>
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Time</th>
                <th>Name</th>
                <th>Client</th>
              </tr>
            </thead>
            <tbody>
              {bookedSlots.map((bookedSlot, index) => (
                <tr key={index}>
                  <td>{new Date(bookedSlot.date).toLocaleDateString()}</td>
                  <td>{bookedSlot.time}</td>
                  <td>{bookedSlot.name}</td>
                  <td>{bookedSlot.client}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default App;
